mapper.select:
SELECT e.emp_no AS empNo, e.emp_name AS empName, e.dept_no AS deptNo, d.dept_name AS deptName, e.status_no AS statusNo, s.status AS statusName, e.emp_phone AS empPhone, e.emp_email AS empEmail, e.emp_regdate AS empRegdate
FROM emp e LEFT JOIN dept d ON e.dept_no = d.dept_no LEFT JOIN emp_status s ON e.status_no = s.status_no
WHERE e.emp_no = #{empNo}
