mapper.select:
SELECT COUNT(*)
FROM SAL
WHERE MONTH_ATTNO = #{monthAttno}

SELECT COUNT(*)
FROM SAL
WHERE EMP_NO      = #{empNo}
  AND MONTH_ATTNO = #{monthAttno}

SELECT S.SAL_NUM AS salNum, S.MONTH_ATTNO AS monthAttno, S.EMP_NO AS empNo, E.EMP_NAME AS empName, D.DEPT_NAME AS deptName, TO_CHAR(S.SAL_DATE, 'YYYY-MM-DD') AS salDate, TO_CHAR(S.SAL_DATE, 'YYYY"년 "MM"월"') AS yearMonthLabel, S.SAL_BASE AS salBase, S.SAL_BONUS AS salBonus, NVL(S.SAL_PLUS, 0) AS salPlus, NVL(S.OVERTIME_PAY, 0) AS overtimePay, (S.INSURANCE + S.TAX) AS deductTotal, S.REALPAY AS realPay
FROM SAL S JOIN EMP E ON S.EMP_NO = E.EMP_NO LEFT JOIN DEPT D ON E.DEPT_NO = D.DEPT_NO
AND TO_CHAR(S.SAL_DATE, 'YYYY-MM') = #{month}
ORDER BY S.EMP_NO ${dir}
ORDER BY E.EMP_NAME ${dir}
ORDER BY D.DEPT_NAME ${dir}
ORDER BY S.SAL_DATE ${dir}, S.EMP_NO ASC
